let textArr = LogTxt.split('\n');
// let textArr = HackerText.split('.');
//console.table(textArr);
let index = 0;
let lastIndex = textArr.length;
let maxHeight = false;
let colors = ['green', 'red', 'cyan'];
// console.log(lastIndex)
// let charsRead = 0;
// var timer = setInterval(updateDisplay, 500);
//let htext = ''
(function loop() {
	var rand = Math.round(Math.random() * (500)) + 10;
	//console.log(rand)
	setTimeout(function() {
					updateDisplay();
					loop();  
	}, rand);
}());

function updateDisplay(){

	//console.log(index, lastIndex)
	if(index < lastIndex){
		let newText = '> '
		newText += textArr[index]
		//console.log(newText)
		let newP = document.createElement("p")
		
		let newContent = document.createTextNode(newText);
		newP.appendChild(newContent);
		let randColor = Math.round(Math.random() * (100));
		console.log(randColor)
		let textColor = colors[0];
		if(randColor > 85) {
			if(randColor > 95){
				textColor = colors[1];
			} else {
			textColor = colors[2];
			}
		}
		newP.setAttribute('class', textColor);
		index += 1;
		// document.body.insertBefore(newP, text);
		document.getElementById("hackertext-wrap").appendChild(newP);

		// reached max lines we want to display, so start removing first line
		if(index > 20){
			maxHeight = true;
			
		}
		if(maxHeight) {
			let screen = document.getElementById("hackertext-wrap")
			screen.removeChild(screen.childNodes[0]);
		}
		//console.log(index)
		// reset index for continuous feed
		if(index === lastIndex) {
			index = 0;		
		}

	}
}